// InputField.js
import React from 'react';
import styled from 'styled-components';
import TextInput from '../components/TextInput'; // TextInput 컴포넌트 가져오기
import SUbButton from '../components/SubButton'; // SubButton 컴포넌트 가져오기

const InputFieldContainer = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center; /* 가로 중앙 정렬 */
    margin-bottom: 16px; /* 각 입력 필드 간의 간격 추가 */
`;

const LabelContainer = styled.div`
    display: flex;
    align-items: center; /* 수직 중앙 정렬 */
`;

const Asterisk = styled.div`
    color: red; /* 별표 색상 지정 */
    margin-left: 4px; /* 별표와 텍스트 간격 */
`;

const InputField = ({ label, id, type, placeholder, showButton }) => {
    return (
        <InputFieldContainer>
            <LabelContainer>
                <div>{label}</div>
                <Asterisk>*</Asterisk>
            </LabelContainer>
            <TextInput
                type={type}
                id={id}
                placeholder={placeholder}
                marginbottom="0px"
                autoFocus
            />
            {showButton && <SUbButton>중복 확인</SUbButton>}
        </InputFieldContainer>
    );
};

export default InputField;
