import React from 'react';
import styled from 'styled-components';

const StyledInput = styled.input`
    width: 300px;
    height: 50px;
    border: 1px solid #e0e0e0;
    border-radius: 5px;
    padding: 0 10px;
    margin-bottom: 10px;
    font-size: 15px;
    transition: border-color 0.1s ease, border-width 0.1s ease;

    &:focus {
        border: 1px solid #828282; /* 포커스 시 테두리 색상과 두께 변경 */
        outline: none; /* 기본 포커스 아웃라인 제거 */
    }
`;

export default function TextInput({ type, id, placeholder, autoFocus }) {
    return (
        <StyledInput
            type={type}
            id={id}
            placeholder={placeholder}
            autoFocus={autoFocus}
        />
    );
}
