import React from 'react';
import styled from 'styled-components';

const StyledButton = styled.button`
    width: 100%;
    height: 50px;
    border: 1px solid #f4a192;
    border-radius: 5px;
    padding: 0 10px;
    color: white;
    font-weight: 600;
    font-size: 15px;
    cursor: pointer;
    margin-bottom: 10px;
    transition: background 0.5s ease, color 0.2s ease; /* 배경 애니메이션 시간 설정 */
    background: ${(props) => (props.primary ? '#F4A192' : 'white')};
    color: ${(props) => (props.primary ? 'white' : '#f4a192')};

    &:hover {
        background: ${(props) =>
            props.primary
                ? 'linear-gradient(to bottom, #EE9484, #F4A192)'
                : 'linear-gradient(to bottom, #f4a192, white'};
        color: white; /* 항상 흰색으로 유지 */
        background-size: 100% 200%; /* 배경 크기 조정 */
        background-position: top; /* 초기 배경 위치 설정 */
    }

    &:active {
        background: ${(props) =>
            props.primary ? '#EE9484' : '#f4a192'}; /* 클릭 시 색상 변경 */
    }
`;

export default function MainButton({ onClick, children, primary }) {
    return (
        <StyledButton onClick={onClick} primary={primary}>
            {children}
        </StyledButton>
    );
}
