// InputField.js
import React from 'react';
import styled from 'styled-components';
import TextInput from '../components/TextInput'; // TextInput 컴포넌트 가져오기
import SUbButton from '../components/SubButton'; // SubButton 컴포넌트 가져오기

const InputFieldContainer = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 20px; /* 각 필드 간의 간격 설정 */
    width: 100%; /* 전체 필드가 가로로 확장되도록 */
`;

const LabelContainer = styled.div`
    display: flex;
    width: 120px;
    align-items: center; /* 라벨과 별표를 수직 중앙 정렬 */
    margin-right: 10px; /* 라벨과 입력 필드 간의 간격 설정 */
`;

const Asterisk = styled.div`
    color: red; /* 별표 색상 지정 */
    margin-left: 4px; /* 별표와 텍스트 간격 */
`;

const InputWrapper = styled.div`
    flex: 1; /* TextInput이 차지할 비율 설정 */
`;

const ButtonWrapper = styled.div`
    display: flex;
    justify-content: flex-start; /* SubButton 좌측 정렬 */
`;

const InputField = ({
    label,
    id,
    type,
    placeholder,
    showButton,
    buttonText,
}) => {
    return (
        <InputFieldContainer>
            <LabelContainer>
                <div>{label}</div>
                <Asterisk>*</Asterisk>
            </LabelContainer>
            <InputWrapper>
                <TextInput
                    type={type}
                    id={id}
                    placeholder={placeholder}
                    marginbottom="0px"
                    autoFocus
                />
            </InputWrapper>
            {showButton && (
                <ButtonWrapper>
                    <SUbButton>{buttonText}</SUbButton> {}
                </ButtonWrapper>
            )}
        </InputFieldContainer>
    );
};

export default InputField;
